  disease         = TRUE,
  test            = c("pos", "neg"),
  p_test_given_dz = c(Se, 1 - Se)
)

stopifnot(abs(sum(test_given_dz$p_test_given_dz) - 1) < 1e-10)

test_given_dz |> show_tbl("Conditional: P(test | disease = TRUE)")
