icer_AB <- results_AB |>
  mutate(rank_cost = rank(EV_cost, ties.method = "first")) |>
  arrange(rank_cost) |>
  mutate(
    dCost = EV_cost - lag(EV_cost),
    dQALY = EV_qaly - lag(EV_qaly),
    ICER  = dCost / dQALY
  )

icer_AB |>
  show_tbl("ICER table", "Computed for the costlier vs cheaper strategy")
